package enzo.Calculos;

public class Calculadora {

	public double somar(double v1, double v2) {
		//return 20.25;
		return v1 + v2;
	}

	public double subtracao(double S1, double S2) {
		// TODO Auto-generated method stub
		return S1 -S2 ;
	}

	public double divisao(double D1, double D2) {
	// TODO Auto-generated method stub
		return D1 /D2 ;
	}

	public double Mult(double M1, double M2) {
		// TODO Auto-generated method stub
		return M1 * M2 ;
	}

}
